import { Component, OnInit } from '@angular/core';
import { CategoryModel } from 'src/app/models/category-model';
import { ProductModel } from 'src/app/models/product-model';
import { ProductsService } from 'src/app/services/products.service';

@Component({
  selector: 'app-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css']
})
export class ListComponent implements OnInit{

  public products: ProductModel[]

  public categories: CategoryModel[]

  public constructor(private productService: ProductsService){ }

  async ngOnInit(): Promise<void> {
    try {
      this.products = await this.productService.getAllProducts()  
      this.categories = await this.productService.getAllCategories()
    } 
    catch (err: any) {
      console.log(err)      
    }    
  }

  public async getProductsByCategory(categoryId:string): Promise<void> {
    try {
      if(!categoryId){
        this.products = await this.productService.getAllProducts()
      }
      this.products = await this.productService.getProductsByCategory(categoryId)  
    }
    catch (err: any) {
      console.log(err)
    }    
  }
}
